PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;

CREATE TABLE IF NOT EXISTS "nene_contact" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "email" varchar(254) NOT NULL, "subject" varchar(200) NOT NULL, "message" text NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "nene_article" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(255) NOT NULL, "author" varchar(100) NOT NULL, "category" varchar(100) NOT NULL, "content" text NOT NULL, "image" varchar(100) NULL, "published_date" date NOT NULL, "slug" varchar(50) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "nene_technology" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(50) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "nene_education" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "degree" varchar(100) NULL, "specialty" varchar(100) NOT NULL, "university" varchar(100) NOT NULL, "start_date" date NOT NULL, "end_date" date NULL, "description" text NOT NULL, "country" varchar(100) NOT NULL, "city" varchar(100) NOT NULL, "employee_id" bigint NOT NULL REFERENCES "nene_employee" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_project_technologies" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "project_id" bigint NOT NULL REFERENCES "nene_project" ("id") DEFERRABLE INITIALLY DEFERRED, "technology_id" bigint NOT NULL REFERENCES "nene_technology" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_experience" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "position" varchar(100) NOT NULL, "company" varchar(100) NOT NULL, "start_date" date NOT NULL, "end_date" date NULL, "is_current" bool NOT NULL, "description" text NOT NULL, "city" varchar(100) NOT NULL, "employee_id" bigint NOT NULL REFERENCES "nene_employee" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_experience_technologies" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "experience_id" bigint NOT NULL REFERENCES "nene_experience" ("id") DEFERRABLE INITIALLY DEFERRED, "technology_id" bigint NOT NULL REFERENCES "nene_technology" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_softskills" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(50) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "nene_employee" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "email" varchar(254) NOT NULL UNIQUE, "phone" varchar(20) NULL, "linkedin" varchar(200) NULL, "position" varchar(150) NOT NULL, "photo" varchar(100) NULL, "location" varchar(100) NOT NULL, "about" text NOT NULL, "slug" varchar(50) NULL UNIQUE, "cv" varchar(100) NULL, "gitHub" varchar(200) NULL, "medium" varchar(200) NULL);
CREATE TABLE IF NOT EXISTS "nene_employee_softSkills" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "employee_id" bigint NOT NULL REFERENCES "nene_employee" ("id") DEFERRABLE INITIALLY DEFERRED, "softskills_id" bigint NOT NULL REFERENCES "nene_softskills" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_project" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL, "description" text NOT NULL, "image" varchar(100) NULL, "employee_id" bigint NOT NULL REFERENCES "nene_employee" ("id") DEFERRABLE INITIALLY DEFERRED, "slug" varchar(50) NULL UNIQUE, "demo" varchar(200) NULL, "depot" varchar(200) NULL, "date" date NOT NULL);
CREATE TABLE IF NOT EXISTS "nene_skill" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "nene_certification_skills" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "certification_id" bigint NOT NULL REFERENCES "nene_certification" ("id") DEFERRABLE INITIALLY DEFERRED, "skill_id" bigint NOT NULL REFERENCES "nene_skill" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "nene_certification" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(150) NOT NULL, "platform" varchar(100) NOT NULL, "link" varchar(200) NULL, "image" varchar(100) NULL, "employee_id" bigint NOT NULL REFERENCES "nene_employee" ("id") DEFERRABLE INITIALLY DEFERRED, "date" date NOT NULL);
CREATE TABLE IF NOT EXISTS "nene_aboutsection" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(255) NOT NULL, "year" varchar(4) NOT NULL, "history_title" varchar(255) NOT NULL, "history_text" text NOT NULL, "video_url" varchar(200) NULL, "mission" text NOT NULL, "image" varchar(100) NULL);
CREATE TABLE IF NOT EXISTS "nene_practice" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(255) NOT NULL, "description" text NOT NULL, "number" integer unsigned NOT NULL UNIQUE CHECK ("number" >= 0));
DELETE FROM sqlite_sequence;
